def make_purchase_email(name, package, initial_message = "Welcome to Socrasica!"):
    email = email_template.replace("[NAME]", name)
    email = email.replace("[PACKAGE_NAME]", package)
    email = email.replace("[INITIAL_MESSAGE]", initial_message)
    return email
